mapper.select:
select m_id, m_name, m_birth, m_gender, m_email, m_phone, m_pw, m_mileage, m_availabled
from member_tbl 
where 

		m_id = #{m_Id}				


		m_email = #{m_Email}

select m_pw 
from member_tbl 
where m_id = #{m_Id}

mapper.update:
update member_tbl set
m_pw = #{m_Pw}				
where m_id = #{m_Id}

update member_tbl set

		m_phone = #{m_Phone}



			, 

		m_email = #{m_Email} 

where m_id = #{m_Id}

mapper.delete:
update member_tbl
set m_availabled = '0'
where m_id = #{m_Id}

mapper.update:
update member_tbl
set m_availabled = '-1'
where m_id =#{m_Id} and m_number=#{m_Number}

update member_tbl
set m_availabled = '1'
where m_id =#{m_Id} and m_number=#{m_Number}

update member_tbl
set m_Mileage = m_Mileage + (
							select (b_price / 10)
						    from booking_tbl b join review_tbl rv 
						    on b.b_no = rv.b_no 
						    where rv.b_no = #{b_No} and rv.m_id = #{m_Id}
						    )
where m_id =#{m_Id}

update member_tbl
set m_Mileage = m_Mileage - #{b_usemileage}
where m_id =#{m_id}
